import { Link } from 'react-router-dom';
import axios from 'axios';

const Allproduct = props =>(
    <div className="bg-white shadow-lg rounded m-8 p-4 md:bg-orange">
          <div className=" hidden sm:block">  
            <img className="w-full md:border-white md:border-solid md:border-4 h-auto " src="https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/605d76cd-d471-4fa0-851b-41c6fc67c919/%E0%B9%80%E0%B8%AA%E0%B8%B7%E0%B9%89%E0%B8%AD%E0%B8%A2%E0%B8%B7%E0%B8%94%E0%B8%9C%E0%B8%B9%E0%B9%89-sportswear-dptPS1.png"/>
         
  
          </div>
          <div className="sm:w-2/3 ">
   
                    <h3 className="text-orange text-xl font-semibold ">Ruben Garza</h3>  
                    
            <p className="text-grey-dark font-thin text-sm leading-normal ">
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatibus quia, nulla! Maiores et perferendis eaque, exercitationem praesentium nihil.
            </p>
          </div>  
