    // <div>
    // <tr>
    //     <td>{props.product.pname}</td>
    //     <td>{props.product.pdesc}</td>
    //     <td>{props.product.brand}</td>
    //     <td>{props.product.pprice}</td>
    // </tr>
    // </div>
)

export default class ProductList extends Component{
    constructor(props){
        super(props);
        this.state = {product: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5000/product/all')
        .then(res =>{
            this.setState({ product: res.data.prod})
            console.log(res.data.prod)
        })
        .catch((error)=>{
            console.log(error);
        })
    }
    productList(){
        console.log(this.state.product);
        if(this.state.product){
            return this.state.product.map(currentproduct=>
                {
                    return <Allproduct product={currentproduct} key={currentproduct._id} />;
                })
        }
    }
    render() {
        return (
            <div className="grid grid-flow-col grid-cols-4 ">
                    {this.productList()}

            </div>
        )
    }
}



