import React ,{ Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
// import { response } from 'express';

const Cartlist = props =>(

    <div className="flex">
      <img className=" w-52 h-52 object-cover justify-self-end " src={props.img} />
      <div className="ml-4">
          <div className="font-primary2 flex">sdssdsd
          <div className=" ml-96">
              sdsadsa</div></div>
          <div className="font-primary3">sdssdsd</div>
          <div className="font-primary2">sdssdsd</div>
      </div>
      </div>

)

export default class ViewProduct extends Component{
    constructor(props){
        super(props);
        this.onClicker = this.onClicker.bind(this);
        this.showCartlist = this.showCartlist.bind(this);
        this.state = {
            cart:[],
            };
                    
    }
    componentDidMount(){
        const accessTokenObj = localStorage.getItem("token");
        const x = accessTokenObj
        const myHeaders = new Headers();
              myHeaders.append("Authorization", x)
            //   myHeaders.append("Content-Type","application/json")
        // fetch('http://localhost:5000/cart/'+this.props.match.params.id,{
        //     method:'Get',
        //     headers : myHeaders
        // })
        // .then(res => console.log(res.data))
        axios.get('http://localhost:5000/cart/'+this.props.match.params.id)
        .then(res=>{
            // console.log(res)
            this.setState({
                cart:res.data
            })
            
        })
        
    }
    onClicker(){
        console.log(this.state.cart)
    }
    showCartlist(){
        if(this.state.cart!==undefined){
            // console.log(this.state.cart[0].prId[0][0].pimg[this.state.cart[0].prcolor][0])
            return this.state.cart.map(currentobj=>{
                let x = currentobj.prId[0][0].pimg[currentobj.prcolor][0]
                console.log(x)
                
                return <div className="m-2 p-0 mr-96 br-64 border-b-2 w-full pb-6 mb-6 ">
                            <Cartlist img={currentobj.prId[0][0].pimg[currentobj.prcolor][0]} />
                            </div>
            })}
    
    }

    render() {
        return (
            <div className="mt-32 mx-96 h-full">
                {/* <button onClick={this.showCartlist} >test</button> */}
                <div className="mr-48">
                    {this.showCartlist()}
                </div>
                    
                
            </div>
            
        )
    }
}
