import React ,{ Component } from 'react';
import axios from 'axios';

export default class Register extends Component{
    constructor(props) {
        super(props);

        this.onChangeFirstname = this.onChangeFirstname.bind(this);
        this.onChangeLastname = this.onChangeLastname.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeDay = this.onChangeDay.bind(this);
        this.onChangeMonth = this.onChangeMonth.bind(this);
        this.onChangeYears = this.onChangeYears.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            firstname: '',
            lastname: '',
            email: '',
            password: '',        
            day:1,
            month:1,
            years:2021,
        }
    }
    onChangeFirstname(e) {
        this.setState({
          firstname: e.target.value
        })
      }
    onChangeLastname(e) {
        this.setState({
          lastname: e.target.value
        })
    }
    onChangeEmail(e) {
        this.setState({
          email: e.target.value
        })
    }
    onChangePassword(e) {
        this.setState({
          password: e.target.value
        })
    }
    onChangeDay(e) {
        this.setState({
          day: e.target.value
        })
    }
    onChangeMonth(e) {
        this.setState({
          month: e.target.value
        })
    }
    onChangeYears(e) {
        this.setState({
          years: e.target.value
        })
    }
    onSubmit(e) {
        e.preventDefault();
    
        const user = {
          firstname: this.state.firstname,
          lastname: this.state.lastname,
          email: this.state.email,
          password: this.state.password,
          bday: this.state.day,
          bmonth: this.state.month,
          byear: this.state.years
        }
    
        console.log(user);

        axios.post('http://localhost:5000/users/register',user)
        .then(res => console.log(res.data));
    
        this.setState({
            firstname: '',
            lastname: '',
            email: '',
            password: '',     

        })
        window.location = '/user';

    }

    render() {
        return (
            <div className= "flex h-screen ">
             <div className="m-auto mt-12">
             <div className="pb-8 font-primary2 text-6xl text-center">
                 REGISTER
             </div>
             <form className="max-w-lg" action="" onSubmit={this.onSubmit}>
             <div className="relative">
             <label for="fn" className="absolute mt-5 ml-2 bg-white px-2 font-primary4  text-gray-800">First name</label>
                 <input id="fn" className="rounded-none appearance-none border  w-full px-4 py-3 mt-8 font-primary3 text-grey-500" type="text" value={this.state.firstname}
                    onChange={this.onChangeFirstname} required/>
                    </div>
            <div className="relative mt-6">
             <label for="ln" className="absolute ml-2 bg-white px-2 font-primary4  text-gray-800">Lastname</label>
             <input id="ln" className="rounded-none appearance-none border  w-full px-4 py-3 mb-6 font-primary3 text-grey-500" type="text" placeholder="Lastname" value={this.state.lastname}
                    onChange={this.onChangeLastname} required/>
                    </div>

                 <input className="rounded-none appearance-none border bg-gray-200 w-full px-4 py-3 mb-6 font-primary3 text-grey-500" type="email" placeholder="Email" value={this.state.email}
                    onChange={this.onChangeEmail} required/>
                 <input className="rounded-none appearance-none border bg-gray-200 w-full px-4 py-3 mb-6 font-primary3 text-grey-500" type="Password" placeholder="Password" value={this.state.password}
                    onChange={this.onChangePassword} required/>
                <input className="rounded-none appearance-none border-gray-500 bg-gray-200 w-1/3 px-4 py-3 border-r mb-6 font-primary3 text-grey-500" type="Number" placeholder="DD" value={this.state.day}
                    onChange={this.onChangeDay} required/>
                <input className="rounded-none appearance-none border-gray-500 bg-gray-200 w-1/3 px-4 py-3 mb-6 font-primary3 border-r text-grey-500" type="Number" placeholder="MM" value={this.state.month}
                    onChange={this.onChangeMonth} required/>
                <input className="rounded-none appearance-none bg-gray-200 border-white border-r-2 w-1/3 px-4 py-3 mb-6 font-primary3 text-grey-500" type="Number" placeholder="YYYY" value={this.state.years}
                    onChange={this.onChangeYears} required/>
                 <input type="checkbox" class="form-checkbox mt-8" required />
                 <span class="ml-2">I agree to the <span class="underline">privacy policy<br></br></span></span>
                 <button className=" rounded-none shadow bg-gray-400 hover:bg-gray-700 my-10 text-white font-bold py-4 px-8 font-primary2  focus:outline-none focus:shadow-outline">Register</button>
                
             </form>
             <div></div>
             </div>
         </div>
        )
    }

}

