                showOptionAsse:false
            })
        }
        else if(e.target.value==="1"){
            this.setState({
                showOptionFoot:true,
                showOptionAppe:false,
                showOptionAsse:false
            })
        }
        else if(e.target.value==="2"){
            this.setState({
                showOptionAppe:true,
                showOptionFoot:false,
                showOptionAsse:false
            })
        }
        else if(e.target.value==="3"){
            this.setState({
                showOptionAsse:true,
