import React ,{ Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
// import { response } from 'express';

const Vproduct = props =>(
    <div className="bg-white shadow-sm rounded m-1 md:bg-orange flex">
    <div>
    <div className="flex">  
      <img className="w-96 mr-6 md:border-white md:border-solid md:border-4 h-auto object-cover" src={props.img[0]}/>
      <img className="w-96 mr-6 md:border-white md:border-solid md:border-4 h-auto object-cover" src={props.img[1]}/>


    </div>
    <div className="flex mt-6">  
    <img className="w-96 mr-6 md:border-white md:border-solid md:border-4 h-auto object-cover" src={props.img[2]}/>
    <img className="w-96 mr-6 md:border-white md:border-solid md:border-4 h-auto object-cover" src={props.img[3]}/>


    </div>
    </div>
    <div className="sm:w-2/3 pt-2 pl-3 pb-2 ml-64 text-right">

      <h3 className="text-orange text-6xl font-primary2 ">{props.product.pname}</h3>  
              
      <p className="text-grey-dark text-sm leading-normal font-primary ">
      {props.product.pdesc}
      </p>
      <p className="text-grey-dark text-2xl mt-10 leading-normal font-primary">
      ฿{props.product.pprice}
      </p>
      <div className="flex ml-96 mt-32">
        <br></br>
      <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">XS</div>
      <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">S</div>
      <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">M</div>
      <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">L</div>
      <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">XL</div>
      <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">2XL</div>
      </div>
      <div className="bg-black shadow text-white rounded-full py-3 px-6 mt-12 ml-96 md:bg-orange text-center cursor-pointer font-primary2">ADD TO CART</div>
    </div>  
</div>

)

export default class ViewProduct extends Component{
    constructor(props){
        super(props);
        this.state = {product: [],
                    img:[]};
        
    }

    componentDidMount(){
        axios.get('http://localhost:5000/product/'+this.props.match.params.id)
        .then(res =>{
            this.setState({ img: res.data.pimg})
            this.setState({ product: res.data})
            console.log(this.state.product[0])
            
        })
    }
    // viewproduct(id){
    //     axios.get('http://http://localhost:5000/product/'+id)
    //     .then(response => {console.log(response.data)})

    //     this.setState({
    //         product: this.state.product.filter(el => el._id !== id)
    //     })
    // }

    productList(){
        console.log(this.state.[0]);
        if(this.state.product){
                    
                    return <Vproduct img={this.state.img} product={this.state.product} />;
        }
    }
    render() {
        return (
            <div className="px-36 pt-16">
                {this.productList()}
            </div>
        )
    }
}
