import React ,{ Component, useState } from 'react';
import axios from 'axios';
import { ConnectionBase } from 'mongoose';
import { fireEvent } from '@testing-library/dom';
import { findAllByTestId } from '@testing-library/dom';

// // Import React FilePond
// import { FilePond, registerPlugin } from "react-filepond";
// import "filepond/dist/filepond.min.css";

// // Import the Image EXIF Orientation and Image Preview plugins
// // Note: These need to be installed separately
// import FilePondPluginImageExifOrientation from "filepond-plugin-image-exif-orientation";
// import FilePondPluginImagePreview from "filepond-plugin-image-preview";
// import "filepond-plugin-image-preview/dist/filepond-plugin-image-preview.css";

// Register the plugins
// registerPlugin(FilePondPluginImageExifOrientation, FilePondPluginImagePreview);

export default class Addprod extends Component{
    constructor(props) {
        super(props);

        this.onChangeProductName = this.onChangeProductName.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeQuantity = this.onChangeQuantity.bind(this);
        this.onChangeTypeQuantity = this.onChangeTypeQuantity.bind(this);
        this.onChangeType = this.onChangeType.bind(this)
        this.onChangeDivision = this.onChangeDivision.bind(this);
        this.onChangeSex = this.onChangeSex.bind(this);
        this.onChangeBrand = this.onChangeBrand.bind(this);
        this.onChangeImg = this.onChangeImg.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.handleOption = this.handleOption.bind(this);
        this.letkillthislove = this.letkillthislove.bind(this);
        this.setNum = this.setNum.bind(this);

        this.state = {
            pname: '',
            pprice: '',
            pdesc: '',
            pquan: [],
            ptquan:[],
            ptype: '',
            pdivn: '',
            psex:'',              
            pbrand: '',      
            pimg: '',
            purl:'' ,
            showOption:false,
            showOptionFoot:false,
            showOptionAppe:false,
            showOptionAsse:false,
            file:[],
            preparefile1:[],
            preparefile2:[],
            preparefile3:[],
            preparefile4:[],
            preparefile5:[],
            fileReady:false,
            errormsg:false,
            memptquan:[],
            ptnamecol:['','','','',''],
            ptcolor: 0,
            ptcolotcrea: []
        }
    }
    // postDetails=()=>{
    //     const data = new FormData()
    //     console.log(this.state.pimg)
    //     data.append("file",this.state.pimg)
    //     data.append("upload_preset","valyoo")
    //     data.append("cloud_name","ProjectWhite")
    //     fetch("https://api.cloudinary.com/v1_1/projectwhite/image/upload",{
    //         method:"post",
    //         body:data
    //     })
    //     .then(res=>res.json())
    //     .then(data =>{
    //         this.setState({
    //             pimg: data.url
    //         })
    //         console.log(this.state.purl)
    //     })
    //     .catch(err=>{
    //         console.log(err)
    //     })
    // }
    handleOption(e){
        console.log(e.target.value)
        if(e.target.value===""){
            this.setState({
                showOptionFoot:false,
                showOptionAppe:false,
                showOptionAsse:false,
                
            })
        }
        else if(e.target.value==="1"){
            let prototypeShoe = [["4US",0,0,0,0,0],["4.5US",0,0,0,0,0],["5US",0,0,0,0,0],["5.5US",0,0,0,0,0],["6US",0,0,0,0,0],["6.5US",0,0,0,0,0],["7US",0,0,0,0,0],["7.5US",0,0,0,0,0],["8US",0,0,0,0,0],["8.5US",0,0,0,0,0],["9US",0,0,0,0,0],["9.5US",0,0,0,0,0],["10US",0,0,0,0,0],["10.5US",0,0,0,0,0],["11US",0,0,0,0,0],["11.5US",0,0,0,0,0],["12US",0,0,0,0,0],["12.5US",0,0,0,0,0],["13US",0,0,0,0,0],["13.5US",0,0,0,0,0],["14US",0,0,0,0,0],["15US",0,0,0,0,0],]
            this.setState({
                showOptionFoot:true,
                showOptionAppe:false,
                showOptionAsse:false,
                pdivn:"shoes",
                memptquan:prototypeShoe
            })
        }
        else if(e.target.value==="2"){
            let prototypeCloth = [["XS",0],["S",0],["M",0],["L",0],["XL",0],["2XL",0],]
