import ReactDOM from 'react-dom'
import React ,{ Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { browserHistory } from 'react-router'
import { confirmAlert } from 'react-confirm-alert'; 
import 'react-confirm-alert/src/react-confirm-alert.css';
import { CountryDropdown, RegionDropdown, CountryRegionData } from 'react-country-region-selector';




export default class Address extends Component{
    
    constructor(props){
        
        
        super(props);

        this.onChangeFirstname = this.onChangeFirstname.bind(this);
        this.onChangeLastname = this.onChangeLastname.bind(this);
        this.onChangeDay = this.onChangeDay.bind(this);
        this.onChangeMonth = this.onChangeMonth.bind(this);
        this.onChangeYears = this.onChangeYears.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.deleteAccount = this.deleteAccount.bind(this);
    
        this.state = {
            user:[],
            firstname:'',
            lastname:'',
            bday:1,
            bmonth:1,
            byear:2021,
            country: '',
            region: '' 

        };
    }
    selectCountry (val) {
      this.setState({ country: val });
    }
  
    selectRegion (val) {
      this.setState({ region: val });
    }

    onChangeFirstname(e) {
        this.setState({
          firstname: e.target.value
        })
      }
    onChangeLastname(e) {
        this.setState({
          lastname: e.target.value
        })
    }
    onChangeDay(e) {
        this.setState({
          bday: e.target.value
        })
    }
    onChangeMonth(e) {
        this.setState({
          bmonth: e.target.value
        })
    }
    onChangeYears(e) {
        this.setState({
          byear: e.target.value
        })
    }
    deleteAccount(e){
        confirmAlert({
            title: 'Are you sure to delete this account?',
            buttons: [
              {
                label: 'Yes',
                onClick: () => {

                    axios.delete('http://localhost:5000/users/d/'+this.props.match.params.id)
                    .then(res => console.log(res.data));
                    localStorage.clear();
                    window.location = '/';
                }
              },
              {
                label: 'No',
                onClick: () => console.log('nothings')
              }
            ]
          });
    }
    onSubmit(e) {
        e.preventDefault();
    


        confirmAlert({
            title: 'UPDATE YOUR PERSONAL INFORMATION?',
            buttons: [
              {
                label: 'Yes',
                onClick: () => {
                    const user = {
                    firstname: this.state.firstname,
                    lastname: this.state.lastname,
                    bday: this.state.bday,
                    bmonth: this.state.bmonth,
                    byear: this.state.byear
                    }
                
                    console.log(user);

                    axios.post('http://localhost:5000/users/q/'+this.props.match.params.id,user)
                    .then(res => console.log(res.data));
                
                    window.location = '/personal/'+this.props.match.params.id;
                }
              },
              {
                label: 'No',
                onClick: () => console.log('nothing')
              }
            ]
          });

    }

    componentDidMount(){
        const accessTokenObj = localStorage.getItem("token");
        const x = accessTokenObj
        axios.get('http://localhost:5000/users/protected/',{headers:{Authorization:x}})
        .then(res=>{
            console.log(res.data)
            this.setState({
                user:res.data,
                firstname:res.data.firstname,
                lastname:res.data.lastname,
                bday:res.data.bday,
                bmonth:res.data.bmonth,
                byear:res.data.byear
            })
        })


    }
    render() {
        return (
            <div className="grid grid-cols-3 mx-48 my-12 font-primary4">
                <div className="col-span-2 px-12 border border-black py-12 grid">
                  <div className="flex">
                  <div className="relative w-1/3 sm:w-full">
                 <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">country</label>
                <CountryDropdown className="rounded-none appearance-none border border-r-0 border-gray-500 w-full px-2 pt-5 py-3 mt-8 font-primary3 text-grey-500" value={this.state.country} onChange={(val) => this.selectCountry(val)} />
                </div>
                <div className="w-1/3 relative sm:w-full">
                 <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">province</label>
                <RegionDropdown className="rounded-none appearance-none border border-gray-500 w-full px-4 pt-5 py-3 mt-8 font-primary3 text-grey-500" country={this.state.country} value={this.state.region} onChange={(val) => this.selectRegion(val)} />

                </div>
                </div>
                <div className="flex">
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">district</label>
                 <input className="rounded-none appearance-none border border-r-0 border-gray-500 w-full px-4 pt-5 py-3 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">subdistrict</label>
                 <input className="rounded-none appearance-none border border-r-0 border-gray-500 w-full px-4 pt-5 py-3 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">postcode</label>
                 <input className="rounded-none appearance-none border border-gray-500 w-full px-4 pt-5 py-6 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                </div>
                <div >
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">Address 1</label>
                 <input className="rounded-none appearance-none border border-gray-500 w-full px-4 pt-5 py-12 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">Address 2</label>
                 <input className="rounded-none appearance-none border border-gray-500 w-full px-4 pt-5 py-3 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                <div className="flex">
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">firstname</label>
                 <input className="rounded-none appearance-none border border-gray-500 w-full px-4 pt-5 py-3 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                <div className="w-1/3 relative sm:w-full">
                <label for="fn" className="absolute top-6 ml-2 bg-black px-2 font-primary2 uppercase text-white">lastname</label>
                 <input className="rounded-none appearance-none border border-gray-500 w-full px-4 pt-5 py-3 mt-8 font-primary3 text-grey-500" type="text"></input>
                </div>
                </div>

                </div>

                </div>


                
                <div className="pl-16">
                    <Link to={"/profile/"+this.props.match.params.id}>
                    <div className="text-center mb-2 py-3 text-lg px-2 inline-block border-2 border-white hover:border-black hover:border-2">My Account</div>
                    </Link>
                    <br></br>
                    <Link to={"/personal/"+this.props.match.params.id}>
                    <div className="text-center mb-2 py-3 text-lg px-2 inline-block border-2 border-white hover:border-black hover:border-2">Personal Information</div>
                    </Link>
                    <br></br>
                    <Link to={"/address/"+this.props.match.params.id}>
                    <div className="text-center mb-2 py-3 text-lg px-2 bg-black text-white inline-block border-2 border-black hover:border-black hover:border-2">Address Book</div>
