import React ,{ Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
// import { response } from 'express';

const Cartlist = props =>(

    <div className="flex text-sm ">
      <img className=" w-48 h-48 object-cover justify-self-end " src={props.img} />
      <div className="ml-4 ">
          <Link to={"/product/"+props.datas._id}><div className="font-primary2 text-left uppercase">{props.datas.pname}</div></Link>
          
          <div className="font-primary4 uppercase">{props.datas.pbrand} {props.datas.pdesc}</div>
          <div className="font-primary3 ">{props.datas.ptcolor[props.position]}</div>
          <form className="relative">

              <select className="rounded-none appearance-none border w-1/2 px-4 py-3 mb-6 font-primary3 text-xs">
              <option value="" selected disabled hidden>Division </option>
                    <option value="1" >Shoes</option>
                    <option value="2" >Clothing</option>
                    <option value="3" >Accessories</option>
              </select>
              <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 absolute top-2 left-132" fill="none" viewBox="0 0 24 24" stroke="currentColor">
  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7" />
</svg>
          </form>
          <div className="font-primary text-base">฿{props.datas.pprice}</div>
      </div>
      
      </div>

)

export default class ViewProduct extends Component{
    constructor(props){
        super(props);
        this.onClicker = this.onClicker.bind(this);
        this.showCartlist = this.showCartlist.bind(this);
        this.state = {
            cart:[],
            };
                    
    }
    componentDidMount(){
        const accessTokenObj = localStorage.getItem("token");
        const x = accessTokenObj
        const myHeaders = new Headers();
              myHeaders.append("Authorization", x)
            //   myHeaders.append("Content-Type","application/json")
        // fetch('http://localhost:5000/cart/'+this.props.match.params.id,{
        //     method:'Get',
        //     headers : myHeaders
        // })
        // .then(res => console.log(res.data))
        axios.get('http://localhost:5000/cart/'+this.props.match.params.id)
        .then(res=>{
            // console.log(res)
            this.setState({
                cart:res.data
            })
            
        })
        
    }
    onClicker(){
        console.log(this.state.cart)
    }
    showCartlist(){
        if(this.state.cart!==undefined){
            // console.log(this.state.cart[0].prId[0][0].pimg[this.state.cart[0].prcolor][0])
            return this.state.cart.map(currentobj=>{
                let x = currentobj.prId[0][0]
                console.log(x)
                
                return <div className="m-2 p-0 mr-96 pr-64 border-b-2 w-full pb-6 mb-6 ">
                            <Cartlist img={currentobj.prId[0][0].pimg[currentobj.prcolor][0]} datas={currentobj.prId[0][0]} position={currentobj.prcolor}/>
                            </div>
            })}
    
    }

    render() {
        return (
            <div className="mt-20 mx-96 h-full flex">
                {/* <button onClick={this.showCartlist} >test</button> */}
                <div className="font-primary2">
                    <div className="font-primary2 text-3xl px-2">CART</div>
                    <div className="font-primary3 text-sm px-2 mb-4">Total (1 items)</div>
                    
                    {this.showCartlist()}
                </div>
                <div className="w-full h-52 border py-4 px-4 ml-8 mt-16 font-primary2 text-xl">ORDER SUMMARY
                <div className="font-primary4 text-sm my-2 flex text-left overflow-clip">1<div className="ml-1">ITEM</div><div className=" w-full text-right">฿2,000</div></div>
                <div className="font-primary4 text-sm my-2 flex text-left overflow-clip">DELIVERIES<div className=" w-full text-right">฿0</div></div>
                <div className="font-primary2 text-base mt-14 flex text-left overflow-clip">TOTAL<div className=" w-full text-right">฿2,000</div></div>
                <div className="bg-black shadow text-white rounded-full py-5 px-6 mt-12 md:bg-orange text-center cursor-pointer font-primary text-base" >CHECK OUT</div>
                </div>
                

                    
                
            </div>
            
        )
    }
}
