import React ,{ Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
// import { response } from 'express';

const Vproduct = props =>(


      <img className="w-16 mr-3 h-16 object-cover" src={props.img} onClick={() => console.log(props.key)}/>

)

export default class ViewProduct extends Component{
    constructor(props){
        super(props);
        this.state = {product: [],
                    img:[],
                    isAuth: false};
                    
    }
    test(){
      console.log('wtf')
    }

    componentDidMount(){
      localStorage.setItem('url','/product/'+this.props.match.params.id);
        axios.get('http://localhost:5000/product/'+this.props.match.params.id)
        .then(res =>{
            this.setState({ img: res.data.pimg})
            this.setState({ product: res.data})
            console.log(this.state.isAuth)
            
        })
        const accessTokenObj = localStorage.getItem("token");
        if(accessTokenObj){
            // myHeaders.append("Authorization", accessTokenObj.toString())
            const x = accessTokenObj
            const myHeaders = new Headers();
            myHeaders.append("Authorization", x)
            this.setState({isAuth:true})
            fetch('http://localhost:5000/users/protected',{
              method : 'Get',
              headers : myHeaders,
            })
            .then(res => res.json())
            .then(data => console.log(data));
          }
          else{
            console.log('no token');
          }
    }
    // viewproduct(id){
    //     axios.get('http://http://localhost:5000/product/'+id)
    //     .then(response => {console.log(response.data)})

    //     this.setState({
    //         product: this.state.product.filter(el => el._id !== id)
    //     })
    // }
    // imgList(){
    //   let i = 0;
    //   while(this.state.img[i]){
    //     i++
    //     return <img className="w-96 mr-6 md:border-white md:border-solid md:border-4 h-auto object-cover" src={this.state.img[i]}/>
    //   }
    // }

    productList(){

        if(this.state.product){
            return this.state.img.map(currentimg=>
              {
                    let x = 1;
                    return <Vproduct img={currentimg} product={this.state.product} isAuth = {this.state.isAuth} key={this.renderData(currentimg)}/>;
              })
        }
    }
    render() {
        return (
            <div className="px-36 pt-12 flex">
                <div className="grid grid-cols-2 justify-center">
                  <div className="col-span-2 relative">
                    <img className="w-max object-cover h-3/5" src={this.state.img[0]} alt="" />
                  </div>
                  <div className="grid grid-cols-6 bottom-4 absolute">
                    {this.productList()} </div>
                    </div>
                <div className="sm:w-2/3 pt-2 pb-2 text-right">

                  <h3 className="text-orange text-6xl font-primary2 ">{this.state.product.pname}</h3>  
                  <p className="text-grey-dark text-sm leading-normal font-primary ">{this.state.product.pdesc}</p>
                  <p className="text-grey-dark text-2xl mt-10 leading-normal font-primary">à¸¿{this.state.product.pprice}</p>
                  <div className="flex ml-24 mt-32">
                  <br></br>
                  <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">XS</div>
                  <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">S</div>
                  <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">M</div>
                  <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">L</div>
                  <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">XL</div>
                  <div class="border rounded-full m-3 h-12 w-12 flex items-center justify-center cursor-pointer font-primary">2XL</div>
                  </div>
                  {this.state.isAuth && <div className="bg-black shadow text-white rounded-full py-3 px-6 mt-12 ml-96 md:bg-orange text-center cursor-pointer font-primary2">ADD TO CART</div>}
                  <Link to="/user">{!this.state.isAuth && <div className="bg-black shadow text-white rounded-full py-3 px-6 mt-12 ml-96 md:bg-orange text-center cursor-pointer font-primary2 " >ADD TO CART</div>}</Link>
                </div>  
</div>
        )
    }
}
